mapper.update:
update balancesheet
set tax_code = #{taxCode,jdbcType=VARCHAR},
  report_date = #{reportDate,jdbcType=INTEGER},
  report_content = #{reportContent,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

